#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir//$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir//$exe" usr/bin/env node "$basedir/../a4f23-word-counter/index.js" $args
  } else {
    & "$basedir//$exe" usr/bin/env node "$basedir/../a4f23-word-counter/index.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "/$exe" usr/bin/env node "$basedir/../a4f23-word-counter/index.js" $args
  } else {
    & "/$exe" usr/bin/env node "$basedir/../a4f23-word-counter/index.js" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
